import React from "react";
import { Link } from "react-router-dom";
import { useProducts } from "../context/ProductContext";
import ProductCard from "../components/ProductCard";

const Home = () => {
  const { products, loading } = useProducts();
  const featuredProducts = products.slice(0, 6); // Show first 6 products

  return (
    <div className="home" style={{ padding: "2rem" }}>
      <div style={{ textAlign: "center", marginBottom: "3rem" }}>
        <h1>Welcome to ThriftStore ðŸ‘•</h1>
        <p style={{ fontSize: "1.2rem", color: "#666" }}>
          Find unique, pre-loved items at the best prices.
        </p>
        <Link to="/shop">
          <button
            style={{
              background: "black",
              color: "white",
              padding: "0.8rem 2rem",
              borderRadius: "8px",
              border: "none",
              cursor: "pointer",
              fontSize: "1rem",
              marginTop: "1rem",
            }}
          >
            Shop Now
          </button>
        </Link>
      </div>

      {!loading && featuredProducts.length > 0 && (
        <div>
          <h2 style={{ marginBottom: "2rem" }}>Featured Products</h2>
          <div
            style={{
              display: "flex",
              gap: "1rem",
              flexWrap: "wrap",
              justifyContent: "center",
            }}
          >
            {featuredProducts.map((product) => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>
          <div style={{ textAlign: "center", marginTop: "2rem" }}>
            <Link to="/shop">
              <button
                style={{
                  background: "transparent",
                  color: "black",
                  padding: "0.7rem 1.5rem",
                  borderRadius: "8px",
                  border: "2px solid black",
                  cursor: "pointer",
                  fontSize: "1rem",
                }}
              >
                View All Products
              </button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
