import React from "react";
import { useCart } from "../context/CartContext";
import { Link } from "react-router-dom";

const Cart = () => {
  const {
    cart,
    addToCart,
    removeFromCart,
    removeProductCompletely,
    clearCart,
    total,
  } = useCart();

  if (cart.length === 0)
    return <h3 style={{ padding: "2rem" }}>Your cart is empty üõí</h3>;

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Your Cart</h2>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "1.5rem",
        }}
      >
        {cart.map((item) => (
          <div
            key={item._id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "12px",
              padding: "1rem",
              boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
            }}
          >
            <img
              src={item.image}
              alt={item.name}
              style={{
                width: "100%",
                height: "200px",
                objectFit: "cover",
                borderRadius: "10px",
              }}
            />
            <h3>{item.name}</h3>
            <p>‚Çπ{item.price}</p>
            <p>Quantity: {item.qty}</p>
            <div style={{ display: "flex", gap: "0.5rem" }}>
              <button onClick={() => removeFromCart(item._id)}>-</button>
              <button onClick={() => addToCart(item)}>+</button>
              <button onClick={() => removeProductCompletely(item._id)}>
                Remove üóëÔ∏è
              </button>
            </div>
          </div>
        ))}
      </div>

      <h3 style={{ marginTop: "1rem" }}>Total: ‚Çπ{total}</h3>

      <div style={{ display: "flex", gap: "1rem", marginTop: "1rem" }}>
        <Link to="/checkout">
          <button
            style={{
              background: "green",
              color: "white",
              padding: "0.7rem 1.5rem",
              borderRadius: "8px",
              border: "none",
              cursor: "pointer",
            }}
          >
            Proceed to Checkout
          </button>
        </Link>
        <button
          onClick={clearCart}
          style={{
            background: "black",
            color: "white",
            padding: "0.7rem 1.5rem",
            borderRadius: "8px",
            border: "none",
            cursor: "pointer",
          }}
        >
          Clear Cart
        </button>
      </div>
    </div>
  );
};

export default Cart;
