import React, { useState } from "react";
import { useCart } from "../context/CartContext";
import { useNavigate } from "react-router-dom";

const Checkout = () => {
  const { cart, total, clearCart } = useCart();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    address: "",
    phone: "",
    payment: "Cash on Delivery",
  });

  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleOrder = (e) => {
    e.preventDefault();

    if (!form.name || !form.address || !form.phone) {
      setStatus("âš ï¸ Please fill all required fields");
      return;
    }


    setStatus("âœ… Order placed successfully!");
    clearCart();

    setTimeout(() => {
      navigate("/shop");
    }, 2000);
  };

  if (cart.length === 0)
    return (
      <h3 style={{ padding: "2rem" }}>
        Your cart is empty ðŸ›’. Add items before checkout!
      </h3>
    );

  return (
    <div style={{ padding: "2rem", maxWidth: "600px", margin: "auto" }}>
      <h2>Checkout</h2>


      <div
        style={{
          margin: "1rem 0",
          border: "1px solid #ccc",
          borderRadius: "10px",
          padding: "1rem",
          background: "#f9f9f9",
        }}
      >
        <h4>Order Summary</h4>
        {cart.map((item) => (
          <div
            key={item._id}
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "0.5rem",
            }}
          >
            <span>
              {item.name} x {item.qty}
            </span>
            <span>â‚¹{item.price * item.qty}</span>
          </div>
        ))}
        <h3>Total: â‚¹{total}</h3>
      </div>


      <form
        onSubmit={handleOrder}
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "0.8rem",
          background: "#fff",
          border: "1px solid #ddd",
          borderRadius: "10px",
          padding: "1rem",
        }}
      >
        <input
          name="name"
          placeholder="Full Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <textarea
          name="address"
          placeholder="Full Address"
          value={form.address}
          onChange={handleChange}
          required
          rows={3}
        />
        <input
          name="phone"
          placeholder="Phone Number"
          value={form.phone}
          onChange={handleChange}
          required
        />
        <select
          name="payment"
          value={form.payment}
          onChange={handleChange}
          style={{ padding: "0.5rem" }}
        >
          <option value="Cash on Delivery">Cash on Delivery</option>
          <option value="Online Payment">Online Payment</option>
        </select>

        <button
          type="submit"
          style={{
            background: "black",
            color: "white",
            padding: "0.7rem",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Confirm Order
        </button>
      </form>

      {status && (
        <p style={{ marginTop: "1rem", fontWeight: "bold" }}>{status}</p>
      )}
    </div>
  );
};

export default Checkout;
