import React, { useState } from "react";
import { useCart } from "../context/CartContext";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const Checkout = () => {
  const { cart, total, clearCart } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: user?.name || "",
    address: user?.address || "",
    phone: user?.phone || "",
    payment: "Cash on Delivery",
  });

  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleOrder = async (e) => {
    e.preventDefault();

    if (!form.name || !form.address || !form.phone) {
      setStatus("âš ï¸ Please fill all required fields");
      return;
    }

    setLoading(true);
    setStatus("Processing your order...");

    // Simulate order processing
    await new Promise(resolve => setTimeout(resolve, 2000));

    setStatus("âœ… Order placed successfully! Redirecting...");
    clearCart();

    setTimeout(() => {
      navigate("/shop");
    }, 2000);
  };

  if (cart.length === 0) {
    return (
      <div style={styles.emptyContainer}>
        <div style={styles.emptyCard}>
          <div style={styles.emptyIcon}>ðŸ›’</div>
          <h2 style={styles.emptyTitle}>Your cart is empty</h2>
          <p style={styles.emptyText}>Add items to your cart before checkout</p>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Checkout</h1>

      <div style={styles.layout}>
        <div style={styles.formSection}>
          <div style={styles.sectionCard}>
            <h2 style={styles.sectionTitle}>Shipping Information</h2>
            <form onSubmit={handleOrder} style={styles.form}>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Full Name *</label>
                <input
                  name="name"
                  placeholder="John Doe"
                  value={form.name}
                  onChange={handleChange}
                  required
                  style={styles.input}
                />
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Phone Number *</label>
                <input
                  name="phone"
                  type="tel"
                  placeholder="+91 1234567890"
                  value={form.phone}
                  onChange={handleChange}
                  required
                  style={styles.input}
                />
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Delivery Address *</label>
                <textarea
                  name="address"
                  placeholder="Street address, City, State, PIN"
                  value={form.address}
                  onChange={handleChange}
                  required
                  rows={4}
                  style={styles.textarea}
                />
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Payment Method</label>
                <select
                  name="payment"
                  value={form.payment}
                  onChange={handleChange}
                  style={styles.select}
                >
                  <option value="Cash on Delivery">Cash on Delivery</option>
                  <option value="Online Payment">Online Payment (UPI/Card)</option>
                </select>
              </div>

              {status && (
                <div style={{
                  ...styles.status,
                  background: status.includes("âœ…") ? "#c6f6d5" : status.includes("âš ï¸") ? "#feebc8" : "#bee3f8",
                  color: status.includes("âœ…") ? "#22543d" : status.includes("âš ï¸") ? "#744210" : "#2c5282",
                }}>
                  {status}
                </div>
              )}

              <button
                type="submit"
                disabled={loading}
                style={styles.submitButton}
              >
                {loading ? "Processing..." : "Place Order"}
              </button>
            </form>
          </div>
        </div>

        <div style={styles.summarySection}>
          <div style={styles.summaryCard}>
            <h2 style={styles.summaryTitle}>Order Summary</h2>

            <div style={styles.itemsList}>
              {cart.map((item) => (
                <div key={item._id} style={styles.orderItem}>
                  <div style={styles.orderItemInfo}>
                    <span style={styles.orderItemName}>{item.name}</span>
                    <span style={styles.orderItemQty}>x {item.qty}</span>
                  </div>
                  <span style={styles.orderItemPrice}>
                    â‚¹{((item.price || 0) * (item.qty || 1)).toLocaleString()}
                  </span>
                </div>
              ))}
            </div>

            <div style={styles.divider}></div>

            <div style={styles.summaryRow}>
              <span>Subtotal</span>
              <span>â‚¹{total.toLocaleString()}</span>
            </div>
            <div style={styles.summaryRow}>
              <span>Shipping</span>
              <span style={styles.free}>FREE</span>
            </div>
            <div style={styles.summaryRow}>
              <span>Tax</span>
              <span>â‚¹0</span>
            </div>

            <div style={styles.divider}></div>

            <div style={styles.totalRow}>
              <span>Total</span>
              <span style={styles.grandTotal}>â‚¹{total.toLocaleString()}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: "calc(100vh - 80px)",
