import React from "react";
import { Link } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useWishlist } from "../context/WishlistContext";

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { wishlist, addToWishlist, removeFromWishlist } = useWishlist();

  const isInWishlist = wishlist.some((item) => item._id === product._id);

  const handleWishlist = () => {
    if (isInWishlist) removeFromWishlist(product._id);
    else addToWishlist(product);
  };

  return (
    <div className="product-card">
      <Link to={`/product/${product._id || product.id}`} style={{ textDecoration: "none", color: "inherit" }}>
        <img
          src={product.image}
          alt={product.name}
          style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "8px", cursor: "pointer" }}
        />
        <h3>{product.name}</h3>
        <p>â‚¹{product.price}</p>
      </Link>
      <div className="buttons">
        <button onClick={() => addToCart(product)}>Add to Cart</button>
        <button onClick={handleWishlist}>
          {isInWishlist ? "ðŸ’– Remove" : "â™¡ Wishlist"}
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
