function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`product-tabpanel-${index}`}
      aria-labelledby={`product-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const ProductDetails = () => {
  const { id: productId } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { addToWishlist, removeFromWishlist, isInWishlist } = useWishlist();
  const [selectedSize, setSelectedSize] = useState('');
  const [tabValue, setTabValue] = useState(0);
  const [offerDialogOpen, setOfferDialogOpen] = useState(false);
  const [offerAmount, setOfferAmount] = useState('');
  const [paymentDialogOpen, setPaymentDialogOpen] = useState(false);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success',
  });
  const { products } = useProducts();
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [direction, setDirection] = useState(0);

  // Ensure images array exists
  useEffect(() => {
    if (product && !product.images && product.image) {
      product.images = [product.image];
    }
  }, [product]);

  // First try to find the product in the products array (by _id or id)
  let product = products.find((p) => p._id === productId || p.id === productId || String(p._id) === productId || String(p.id) === productId);

  // If not found, try to find it in the featuredItems array
  if (!product) {
    product = featuredItems.find((p) => p.id === Number(productId) || String(p.id) === productId);
  }

  if (!product) {
    return (
      <Container>
        <Typography variant="h5">Product not found</Typography>
        <Button
          variant="contained"
          onClick={() => navigate('/')}
          sx={{ mt: 2 }}
        >
          Return to Home
        </Button>
      </Container>
    );
  }

  // At this point, product is guaranteed to be defined
  const safeProduct = product;

  // Ensure images array exists for products from API
  if (safeProduct && !safeProduct.images && safeProduct.image) {
    safeProduct.images = [safeProduct.image];
  }

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleBuyNow = () => {
    if (safeProduct.sizes && safeProduct.sizes.length > 0 && !selectedSize) {
      setSnackbar({
        open: true,
        message: 'Please select a size',
        severity: 'error',
      });
      return;
    }
    setPaymentDialogOpen(true);
  };

  const handlePaymentSuccess = () => {
    // Add item to cart after successful payment
    addToCart({
      _id: safeProduct.id || safeProduct._id,
      id: safeProduct.id,
      name: safeProduct.title || safeProduct.name,
      title: safeProduct.title,
      price: safeProduct.price,
      image: safeProduct.images ? safeProduct.images[0] : safeProduct.image,
      qty: 1,
      size: selectedSize,
    });

    setSnackbar({
      open: true,
      message: 'Payment successful! Item added to your orders.',
      severity: 'success',
    });

    // Close payment dialog
    setPaymentDialogOpen(false);

    // Navigate to orders page
    navigate('/orders');
  };

  const handlePaymentError = (error) => {
    setSnackbar({
      open: true,
      message: `Payment failed: ${error}`,
      severity: 'error',
    });
  };

  const handleMakeOffer = () => {
    if (safeProduct.sizes && safeProduct.sizes.length > 0 && !selectedSize) {
      setSnackbar({
        open: true,
        message: 'Please select a size',
        severity: 'error',
      });
      return;
    }
    setOfferDialogOpen(true);
  };

  const handleOfferSubmit = () => {
    const offer = parseFloat(offerAmount);
    if (isNaN(offer) || offer <= 0) {
      setSnackbar({
        open: true,
        message: 'Please enter a valid offer amount',
        severity: 'error',
      });
      return;
    }

    // Here you would typically make an API call to submit the offer
    setSnackbar({
      open: true,
      message: 'Offer submitted successfully',
      severity: 'success',
    });
    setOfferDialogOpen(false);
    setOfferAmount('');
  };

  const handleSave = () => {
    const productId = safeProduct.id || safeProduct._id;
    if (isInWishlist(productId)) {
      removeFromWishlist(productId);
      setSnackbar({
        open: true,
        message: 'Item removed from wishlist',
        severity: 'info',
      });
    } else {
      addToWishlist({
        _id: safeProduct._id,
        id: safeProduct.id,
        name: safeProduct.title || safeProduct.name,
        title: safeProduct.title,
        price: safeProduct.price,
        image: safeProduct.images ? safeProduct.images[0] : safeProduct.image,
      });
      setSnackbar({
        open: true,
        message: 'Item added to wishlist',
        severity: 'success',
      });
    }
  };

  const handleShare = async () => {
    try {
      if (navigator.share) {
        await navigator.share({
          title: safeProduct.title,
          text: `Check out this ${safeProduct.title} on ThriftStore!`,
          url: window.location.href,
        });
      } else {
        // Fallback for browsers that don't support the Web Share API
        await navigator.clipboard.writeText(window.location.href);
        setSnackbar({
          open: true,
          message: 'Link copied to clipboard',
          severity: 'success',
        });
      }
    } catch (error) {
      console.error('Error sharing:', error);
    }
  };

  const handleBack = () => {
    navigate(-1);
