    } else {
      addToWishlist({
        _id: safeProduct._id,
        id: safeProduct.id,
        name: safeProduct.title || safeProduct.name,
        title: safeProduct.title,
        price: safeProduct.price,
        image: safeProduct.images ? safeProduct.images[0] : safeProduct.image,
