                <motion.div
                  key={currentImageIndex}
                  custom={direction}
                  variants={imageVariants}
                  initial="enter"
                  animate="center"
                  exit="exit"
                  transition={{
                    x: { type: "spring", stiffness: 300, damping: 30 },
                    opacity: { duration: 0.2 }
                  }}
                  style={{ position: 'relative', width: '100%' }}
                >
                  <Box
                    component="img"
                    src={safeProduct.images ? safeProduct.images[currentImageIndex] : safeProduct.image}
                    alt={safeProduct.title || safeProduct.name}
                    sx={{
                      width: '100%',
                      height: 'auto',
                      borderRadius: 2,
                      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                      cursor: 'pointer',
                    }}
                    onClick={handleFullscreenToggle}
                  />
                </motion.div>

                {/* Navigation Arrows */}
                {safeProduct.images && safeProduct.images.length > 1 && (
                  <Box 
                    sx={{ 
                      position: 'absolute', 
                      top: '50%', 
                      left: 0, 
                      right: 0, 
                      display: 'flex', 
                      justifyContent: 'space-between',
                      transform: 'translateY(-50%)',
                      px: 1,
                      zIndex: 2
                    }}
                  >
                    <IconButton 
                      onClick={handlePrevImage}
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.7)', 
                        '&:hover': { bgcolor: 'rgba(255,255,255,0.9)' } 
                      }}
                    >
                      <ArrowBackIcon />
                    </IconButton>
                    <IconButton 
                      onClick={handleNextImage}
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.7)', 
                        '&:hover': { bgcolor: 'rgba(255,255,255,0.9)' } 
                      }}
                    >
                      <ArrowForward />
                    </IconButton>
                  </Box>
                )}

                {/* Image Counter */}
                {safeProduct.images && safeProduct.images.length > 1 && (
                  <Box 
                    sx={{ 
                      position: 'absolute', 
                      bottom: 16, 
                      right: 16, 
                      bgcolor: 'rgba(0,0,0,0.6)', 
