              <Box sx={{ display: 'flex', gap: 1, mb: 3 }}>
                <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                  <Button
                    variant="outlined"
                    startIcon={<Favorite />}
                    fullWidth
                    onClick={handleSave}
                    color={isInWishlist(safeProduct.id || safeProduct._id) ? 'error' : 'primary'}
                  >
                    {isInWishlist(safeProduct.id || safeProduct._id) ? 'Saved' : 'Save'}
                  </Button>
                </motion.div>
                <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                  <Button
                    variant="outlined"
                    startIcon={<Share />}
                    fullWidth
                    onClick={handleShare}
                  >
                    Share
                  </Button>
