                  sx={{ 
                    bgcolor: 'rgba(255,255,255,0.2)', 
                    color: 'white',
                    '&:hover': { bgcolor: 'rgba(255,255,255,0.4)' } 
                  }}
                >
                  <ArrowForward />
                </IconButton>
              </Box>

              {/* Image Counter */}
              <Box 
                sx={{ 
                  position: 'absolute', 
                  bottom: 16, 
                  left: '50%', 
                  transform: 'translateX(-50%)', 
                  bgcolor: 'rgba(0,0,0,0.6)', 
                  color: 'white',
                  px: 2,
                  py: 0.5,
                  borderRadius: 1,
                  fontSize: '1rem'
                }}
              >
                {currentImageIndex + 1} / {safeProduct.images.length}
              </Box>
            </>
          )}
        </Box>
      </Dialog>

      {/* Offer Dialog */}
      <Dialog open={offerDialogOpen} onClose={() => setOfferDialogOpen(false)}>
        <DialogTitle>Make an Offer</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Offer Amount (â‚¹)"
            type="number"
            fullWidth
            value={offerAmount}
            onChange={(e) => setOfferAmount(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOfferDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleOfferSubmit} variant="contained">
            Submit Offer
          </Button>
        </DialogActions>
      </Dialog>

      {/* Payment Gateway Dialog */}
      <PaymentGateway
        open={paymentDialogOpen}
        onClose={() => setPaymentDialogOpen(false)}
        amount={safeProduct.price}
        onSuccess={handlePaymentSuccess}
        onError={handlePaymentError}
      />

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default ProductDetails;
